import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import axios from "axios";
import UpdateVideo from "./UpdateVideo";

const backendURL = "http://localhost:5000";

function Home() {
  const [videos, setVideos] = useState([]);
  const [file, setFile] = useState(null);
  const [previewURL, setPreviewURL] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    axios
      .get(`${backendURL}/videos`)
      .then((res) => setVideos(res.data))
      .catch((err) => console.error("Error fetching videos:", err));
  }, []);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setPreviewURL(URL.createObjectURL(selectedFile));
  };

  const uploadVideo = async () => {
    if (!file) return alert("Please select a video file");

    setUploading(true);
    setProgress(0);

    const formData = new FormData();
    formData.append("video", file);

    try {
      await axios.post(`${backendURL}/upload`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
        onUploadProgress: (progressEvent) => {
          setProgress(Math.round((progressEvent.loaded * 100) / progressEvent.total));
        },
      });

      alert("Video uploaded successfully!");
      setVideos([...videos, { filename: file.name }]);
      setFile(null);
      setPreviewURL(null);
    } catch (error) {
      console.error("Upload Error:", error);
      alert("Error uploading video");
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="p-6 max-w-5xl mx-auto min-h-screen bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500 text-white rounded-lg shadow-lg">
      <h1 className="text-4xl font-extrabold text-center mb-6">üìΩÔ∏è Video Streaming App</h1>

      <div className="bg-white p-6 rounded-lg shadow-md text-black">
        <input
          type="file"
          accept="video/*"
          onChange={handleFileChange}
          className="block w-full mb-4 border border-gray-300 rounded-lg p-2"
        />

        {previewURL && <video src={previewURL} controls className="w-full max-w-md mx-auto mb-4 rounded-lg shadow-lg" />}

        <button
          onClick={uploadVideo}
          className="w-full px-4 py-2 bg-gradient-to-r from-blue-600 to-green-400 text-white font-bold rounded-lg"
          disabled={uploading}
        >
          {uploading ? `Uploading... ${progress}%` : "Upload Video"}
        </button>

        {uploading && <div className="w-full bg-gray-300 h-2 mt-3"><div className="bg-green-500 h-2" style={{ width: `${progress}%` }}></div></div>}
      </div>

      <div className="mt-6">
        {videos.length === 0 ? (
          <p className="text-center text-gray-200">No videos available</p>
        ) : (
          videos.map((video) => (
            <div key={video._id} className="mt-6 bg-white p-6 rounded-lg shadow-md text-black">
              <h3 className="text-lg font-semibold">{video.filename}</h3>
              <video src={`${backendURL}/videos/${video.filename}`} controls className="w-full max-w-md mx-auto mb-4 rounded-lg shadow-lg" />
              <div className="flex justify-between mt-2">
                <Link to={`/update/${video._id}`} className="px-4 py-2 bg-yellow-500 text-sm font-semibold rounded-lg">Update</Link>
                <button className="px-4 py-2 bg-red-600 text-sm font-semibold rounded-lg" onClick={() => setVideos(videos.filter((v) => v._id !== video._id))}>
                  Remove
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/update/:id" element={<UpdateVideo />} />
      </Routes>
    </Router>
  );
}
