import { useState, useEffect } from "react";
import axios from "axios";

export default function App() {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [file, setFile] = useState(null);

  // Fetch Videos
  useEffect(() => {
    axios.get("http://localhost:5000/videos").then((res) => {
      setVideos(res.data);
      if (res.data.length > 0) setSelectedVideo(res.data[0].filename);
    });
  }, []);

  // Upload Video
  const uploadVideo = async (e) => {
    e.preventDefault();
    if (!file) return alert("Please select a file");
    
    const formData = new FormData();
    formData.append("video", file);

    await axios.post("http://localhost:5000/upload", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });

    alert("Video uploaded successfully!");
    window.location.reload(); // Reload to show new video
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-500 to-pink-500 p-6 text-white">
      <h1 className="text-3xl font-bold text-center">ðŸŽ¥ Video Streaming App</h1>

      {/* Upload Form */}
      <form onSubmit={uploadVideo} className="mt-6 flex flex-col items-center">
        <input type="file" onChange={(e) => setFile(e.target.files[0])} className="p-2 border rounded" required />
        <button type="submit" className="mt-2 bg-green-500 px-4 py-2 rounded hover:bg-green-600">Upload Video</button>
      </form>

      {/* Video Player */}
      {selectedVideo && (
        <div className="mt-6 flex justify-center">
          <video controls autoPlay className="w-2/3 rounded-lg shadow-lg">
            <source src={`http://localhost:5000/videos/${selectedVideo}`} type="video/mp4" />
          </video>
        </div>
      )}

      {/* Video List */}
      <div className="mt-6 grid grid-cols-2 md:grid-cols-4 gap-4">
        {videos.map((video) => (
          <button
            key={video._id}
            onClick={() => setSelectedVideo(video.filename)}
            className="bg-gray-800 p-2 rounded-lg hover:bg-gray-600"
          >
            {video.filename}
          </button>
        ))}
      </div>
    </div>
  );
}
