import React, { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [videos, setVideos] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      const response = await axios.get("http://localhost:5000/videos");
      setVideos(response.data);
    } catch (error) {
      console.error("Error fetching videos:", error);
    }
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append("video", selectedFile);

    try {
      await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setSelectedFile(null);
      fetchVideos(); // Refresh the video list
    } catch (error) {
      console.error("Error uploading video:", error);
    }
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">Video Streaming App</h1>

      {/* Upload Video */}
      <div className="mb-4">
        <input type="file" onChange={handleFileChange} className="mb-2" />
        <button
          onClick={handleUpload}
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          Upload Video
        </button>
      </div>

      {/* Display Uploaded Videos */}
      <h2 className="text-2xl font-semibold mb-2">Uploaded Videos</h2>
      <div className="grid grid-cols-1 gap-4">
        {videos.map((video) => (
          <div key={video._id} className="p-4 border rounded">
            <h3 className="text-lg font-medium">{video.filename}</h3>
            <video controls width="100%">
              <source src={`http://localhost:5000/videos/${video.filename}`} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
