import React, { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [videos, setVideos] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      const response = await axios.get("http://localhost:5000/videos");
      setVideos(response.data);
    } catch (error) {
      console.error("Error fetching videos:", error);
    }
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select a file first!");
      return;
    }

    const formData = new FormData();
    formData.append("video", selectedFile);

    try {
      await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setSelectedFile(null);
      fetchVideos();
    } catch (error) {
      console.error("Upload error:", error.response ? error.response.data : error);
    }
  };

  return (
    <div className="p-6 max-w-4xl mx-auto bg-gray-100 min-h-screen rounded-lg shadow-lg">
      <h1 className="text-4xl font-bold mb-6 text-center text-blue-600">Video Streaming App</h1>
      
      <div className="bg-white p-6 rounded-lg shadow-md mb-6">
        <input type="file" onChange={handleFileChange} className="mb-3" />
        <button
          onClick={handleUpload}
          className="bg-blue-500 text-white px-5 py-2 rounded-lg hover:bg-blue-600"
        >
          Upload Video
        </button>
      </div>
      
      <h2 className="text-2xl font-semibold mb-4">Uploaded Videos</h2>
      {videos.length === 0 && <p className="text-gray-500">No videos uploaded yet.</p>}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {videos.map((video) => (
          <div key={video._id} className="bg-white p-4 rounded-lg shadow-md">
            <h3 className="text-lg font-medium text-gray-700">{video.filename}</h3>
            <video controls className="w-full mt-2 rounded-lg">
              <source src={`http://localhost:5000/videos/${video.filename}`} type="video/mp4" />
            </video>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
