import React, { useState, useEffect } from "react";
import axios from "axios";

const backendURL = "http://localhost:5000";

function App() {
  const [videos, setVideos] = useState([]);
  const [file, setFile] = useState(null);

  // âœ… Fetch Videos from Backend
  useEffect(() => {
    axios.get(`${backendURL}/videos`).then((res) => setVideos(res.data));
  }, []);

  // âœ… Upload Video
  const uploadVideo = async () => {
    if (!file) return alert("Please select a video file");
    
    const formData = new FormData();
    formData.append("video", file);

    await axios.post(`${backendURL}/upload`, formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });

    window.location.reload();
  };

  return (
    <div className="p-6 max-w-4xl mx-auto bg-gradient-to-r from-purple-500 via-pink-500 to-red-500 min-h-screen rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold text-white text-center mb-4">ðŸŽ¥ Video Streaming App</h1>

      {/* Upload Video */}
      <input type="file" accept="video/*" onChange={(e) => setFile(e.target.files[0])} className="block mb-4 text-white" />
      <button onClick={uploadVideo} className="px-4 py-2 bg-blue-600 text-white rounded">Upload</button>

      {/* Display Videos */}
      <div className="mt-6">
        {videos.length === 0 ? (
          <p className="text-white">No videos available</p>
        ) : (
          videos.map((video) => (
            <div key={video._id} className="mt-4">
              <h3 className="text-lg text-white">{video.filename}</h3>
              <video controls autoPlay className="w-full rounded-lg shadow-md">
                <source src={`${backendURL}/videos/${video.filename}`} type="video/mp4" />
                Your browser does not support the video tag.
              </video>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default App;
